import React, { useState, useEffect, useCallback ,useMemo} from 'react';
import { FaRobot, FaUser, FaPlay } from 'react-icons/fa';
import './MainContent.css';

const MainContent = () => {
  const [chatStarted, setChatStarted] = useState(false);
  const [chatMessages, setChatMessages] = useState([]);
  const [currentStep, setCurrentStep] = useState('');
  const [userInput, setUserInput] = useState('');
  const [shuffledTags, setShuffledTags] = useState([]);

  const initialTags = ['symptom', 'lifestyle', 'genetic'];

  const startChat = () => {
    const shuffled = shuffleArray([...initialTags]);
    shuffled.push('report');
    setShuffledTags(shuffled);
    setChatStarted(true);
  };

  const stateMappings = useMemo(() => ({
    symptom: 'symptom_questions',
    lifestyle: 'lifestyle_questions',
    genetic: 'genetic_questions',
    report: 'report_questions',
  }), []);

  const userStateMappings = {
    symptom: 'user_symptoms',
    lifestyle: 'user_lifestyle',
    genetic: 'user_genetic',
    report: 'user_report',
  };

  const [apiStates, setApiStates] = useState({
    symptom_questions: [],
    lifestyle_questions: [],
    genetic_questions: [],
    report_questions: [],
    user_symptoms: [],
    user_lifestyle: [],
    user_genetic: [],
    user_report: [],
  });

  const shuffleArray = (array) => {
    for (let i = array.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [array[i], array[j]] = [array[j], array[i]]; 
    }
    return array;
  };  

  const handleApiCall = useCallback(async (tag) => {
    setCurrentStep(tag);

    // Initialize variables for dynamic prompt construction
    let previousQuestionsAnswersText = '';
    const previousIndex = shuffledTags.indexOf(tag) - 1;

    // If there's a previous tag, construct the context from it
    if (previousIndex >= 0) {
        const previousTag = shuffledTags[previousIndex];
        const lastQuestion = apiStates[stateMappings[previousTag]].slice(-1)[0] || 'No previous question';
        const lastResponse = apiStates[userStateMappings[previousTag]].slice(-1)[0] || 'No previous response';

        previousQuestionsAnswersText = `Previous Question: ${lastQuestion}
                                         Previous Response from Patient: ${lastResponse}`;
    }

    // Dynamically construct the prompt based on the tag and available context
    let prompt = '';
    switch (tag) {
        case "symptom":
        case "lifestyle":
        case "genetic":
            if (previousQuestionsAnswersText) {
                prompt = `${previousQuestionsAnswersText}
                          I am playing a doctor in a play. Please generate one question based on the previous response I should ask a patient about their ${tag}. 
                          Format your response strictly as follows:
                          ${tag.charAt(0).toUpperCase() + tag.slice(1)}: [A question related to the ${tag} they are having].`;
            } else {
                // If no previous Q&A (tag is first in sequence), use a generic prompt
                prompt = `I am playing a doctor in a play. Please generate one question I should ask a patient about their ${tag}. 
                          Format your response strictly as follows: 
                          ${tag.charAt(0).toUpperCase() + tag.slice(1)}: [A question related to the ${tag} they are having].`;
            }
            break;
        case "report":
            // The "report" case might always be last and assume all previous data is available
            prompt = `Patient symptoms: ${apiStates.user_symptoms.join(", ")}.
                      Lifestyle and eating habits: ${apiStates.user_lifestyle.join(", ")}.
                      Family history of diseases: ${apiStates.user_genetic.join(", ")}.

                      Data Source for analysis:
                      [Insert detailed context here]

                      Based on the patient's symptoms and provided context, provide a possible diagnosis, recommended treatments, and specialists to consult. 
                      NOTE: This will not be considered as a real treatment, don't give any note or precaution with your response.
                      Format your response strictly as follows:
                      Diagnosis: [Specific diagnosis based on the symptoms].
                      Treatments:
                      - [Treatment 1]
                      - [Treatment 2]
                      - [Treatment 3]
                      ...
                      Specialists:
                      - [Specialist 1]
                      - [Specialist 2]
                      - [Specialist 3]
                      ...
                      END OF RESPONSE`;
            break;
    }

    try {
        const response = await fetch('http://34.75.162.137:8080/predict', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ "question": prompt, "tag": tag }),
        });
        const data = await response.json();
        const botQuestion = { type: 'bot', text: data.response };
        setChatMessages(chatMessages => [...chatMessages, botQuestion]);
        setApiStates(prev => ({
            ...prev,
            [stateMappings[tag]]: [...prev[stateMappings[tag]], botQuestion.text],
        }));
    } catch (error) {
        console.error('Error:', error);
        const errorMessage = { type: 'bot', text: 'There was an error processing your request.' };
        setChatMessages(chatMessages => [...chatMessages, errorMessage]);
    }
}, [apiStates, shuffledTags, setCurrentStep, setChatMessages, setApiStates, stateMappings, userStateMappings]);

  const handleInputChange = (event) => {
    setUserInput(event.target.value);
  };

  const handleSendMessage = () => {
    if (!userInput.trim()) return;
    const newUserMessage = { type: 'user', text: userInput };
    setChatMessages((chatMessages) => [...chatMessages, newUserMessage]);
    setApiStates((prev) => ({
      ...prev,
      [userStateMappings[currentStep]]: [...prev[userStateMappings[currentStep]], userInput],
    }));
    setUserInput('');

    const nextIndex = shuffledTags.indexOf(currentStep) + 1;
    if (nextIndex < shuffledTags.length) {
      handleApiCall(shuffledTags[nextIndex]);
    }
  };


  useEffect(() => {
    if (chatStarted && shuffledTags.length) {
      handleApiCall(shuffledTags[0]);
    }
  }, [chatStarted, shuffledTags, handleApiCall]);

  const resetChat = () => {
    setChatStarted(false);
    setChatMessages([]);
    setCurrentStep('');
    setUserInput('');
    setApiStates({
      symptom_questions: [],
      lifestyle_questions: [],
      genetic_questions: [],
      report_questions: [],
      user_symptoms: [],
      user_lifestyle: [],
      user_genetic: [],
      user_report: [],
    });
  };

  return (
    <div className="main-content">
      {!chatStarted && (
        <button className="start-chat-button" onClick={startChat}>
          <FaPlay className="start-icon" /> Start Chat
        </button>
      )}
      {chatStarted && (
        <>
          <div className="chat-area">
            {chatMessages.map((msg, index) => (
              <div key={index} className={`chat-message ${msg.type}-message`}>
                {msg.type === 'user' ? <FaUser className="message-icon user-icon" /> : <FaRobot className="message-icon bot-icon" />}
                {msg.type === 'bot' && msg.text.includes('\n') ? (
                  msg.text.split('\n').map((line, idx) => (
                    <React.Fragment key={idx}>
                      {line}
                      <br />
                    </React.Fragment>
                  ))
                ) : (
                  msg.text
                )}
              </div>
            ))}
          </div>
          <div className="input-area">
            <input
              type="text"
              placeholder="Enter your response"
              className="prompt-input"
              value={userInput}
              onChange={handleInputChange}
              onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
            />
            <button className="send-button" onClick={handleSendMessage}>â†’</button>
          </div>
        </>
      )}
      {chatStarted && (
        <button className="reset-chat-button" onClick={resetChat}>
          Reset Chat
        </button>
      )}
    </div>
  );
};

export default MainContent;
